$(document).ready(function(){
  var state = 0;
  $("a.categories-button").mouseenter(function(){
    $("div.large-categories").fadeIn();
    $("div.large-categories").css("display","flex");

    state = 1;
  });
  $("div.large-categories").mouseleave(function(){
    $("div.large-categories").slideUp();
  });

  $("a.down-button").click(function(){
    var body = $("html, body");
    body.stop().animate({scrollTop:($(".index").height() - 10)}, 500, 'swing', function() {});
  });
  var menuBool = true;
  $("a.product-image").mouseenter(function(){
    $(this).find("img.front-img").hide();
    $(this).find("img.back-img").fadeIn(200);
  });
  $("a.product-image").mouseleave(function(){
    $(this).find("img.back-img").hide();
    $(this).find("img.front-img").fadeIn(200);
  });
  $("a#shopping-bag").mouseenter(function(){
    $("div.cardGUI").slideDown();
    $("div.cardGUI").css("display","flex");
  });
  $("div.cardGUI").mouseenter(function(){
    $("div.cardGUI").slideDown();
    $("div.cardGUI").css("display","flex");
    menuBool = false;
  });
  $("a#shopping-bag").mouseleave(function(){
    $("div.cardGUI").slideUp(100);
  });
  $("div.cardGUI").mouseleave(function(){
    menuBool = true;
    setTimeout(function(){
      if (menuBool == true){
        $("div.cardGUI").slideUp(100);
      }
    }, 500);
  });
  var menu = $("nav");
  $(window).scroll(function(){
    if ($(window).scrollTop() > (100)){
      $("div.large-categories").css("top", ($(document).scrollTop() + menu.height()));
      $("div.cardGUI").css("top", ($(document).scrollTop() + menu.height()));
      menu.css("position","fixed");
      menu.css("top", 0);
      $("a.down-button").hide();
    }else{
      menu.css("position","relative");
      $("div.large-categories").css("top", ($(document).scrollTop() + menu.height() +35));
      $("div.cardGUI").css("top", ($(document).scrollTop() + menu.height() + 35));
      $("a.down-button").show();
    }
  });
});
